import{_ as t,c as o,o as e,a4 as r}from"./chunks/framework.DBIahkuf.js";const a="/AINotes/assets/5d3f36b0391641968ee313492057ba55tplv-k3u1fbpfcp-zoom-in-crop-mark1512000.CmXmGeE4.webp",u=JSON.parse('{"title":"问题","description":"","frontmatter":{},"headers":[],"relativePath":"Document/AI绘画工具/问题.md","filePath":"Document/AI绘画工具/问题.md","lastUpdated":1708941732000}'),n={name:"Document/AI绘画工具/问题.md"},s=r('<h1 id="问题" tabindex="-1">问题 <a class="header-anchor" href="#问题" aria-label="Permalink to &quot;问题&quot;">​</a></h1><h2 id="判断是否是ai生成的图片" tabindex="-1">判断是否是AI生成的图片 <a class="header-anchor" href="#判断是否是ai生成的图片" aria-label="Permalink to &quot;判断是否是AI生成的图片&quot;">​</a></h2><p><a href="https://zhuanlan.zhihu.com/p/640692731" target="_blank" rel="noreferrer">AI被自己骗了！生成照骗轻松逃过AI鉴别器法眼，马斯克机器女友、3米巨人都「成真」了 - 知乎 (zhihu.com)</a></p><p><a href="https://www.reddit.com/r/midjourney/comments/151ko7m/real_or_ai/" target="_blank" rel="noreferrer"><strong>⋙ Reddit @Midjourney</strong></a></p><p><img src="'+a+'" alt="img"></p><p>这是 Reddit 论坛 Midjourney 子区一个非常热门的讨论：判断上面两张图是 AI 生成的，还是真实拍摄的。根据经验我们几乎可以快速判定，这是AI生成的图片！</p><p>但如果让你说出几条理由，是不是就卡壳了？因为这跟真实世界的图片太像了！原帖评论区的大神们，给出了非常细节的判断依据，我们一起来学习下：</p><blockquote><ol><li><strong>皮肤</strong>太平滑无瑕疵，看起来不自然</li><li><strong>五官和面部</strong>特征重复率高，缺乏细微差异</li><li><strong>牙齿、耳朵</strong>等部位存在反常的绘制错误</li><li><strong>灯光和阴影</strong>不协调，不同方向的光源</li><li>背景物体<strong>透视</strong>对不准，存在失真</li><li>存在马赛克和图像边缘毛刺等制作<strong>瑕疵</strong></li><li>衣服和背景缺乏质感和细节，<strong>图案和阴影不协调</strong></li><li>衣服与身体相接的部分处理不自然，<strong>没有厚度和遮挡感</strong></li><li><strong>分辨率和清晰度</strong>比当时胶片摄影水平要高，而非胶片质感，缺乏胶片风格的磨砂质感</li></ol></blockquote>',8),i=[s];function l(c,_,d,p,m,g){return e(),o("div",null,i)}const f=t(n,[["render",l]]);export{u as __pageData,f as default};
