import{_ as l,c as i,o,a4 as t}from"./chunks/framework.DBIahkuf.js";const _=JSON.parse('{"title":"归类总结","description":"","frontmatter":{},"headers":[],"relativePath":"Document/各行业AI工具集/归类总结.md","filePath":"Document/各行业AI工具集/归类总结.md","lastUpdated":1690701750000}'),n={name:"Document/各行业AI工具集/归类总结.md"},r=t('<h1 id="归类总结" tabindex="-1">归类总结 <a class="header-anchor" href="#归类总结" aria-label="Permalink to &quot;归类总结&quot;">​</a></h1><h2 id="这篇文章总结了当前的18种主流方法-并附上了相关教程" tabindex="-1">这篇文章总结了当前的18种主流方法，并附上了相关教程 <a class="header-anchor" href="#这篇文章总结了当前的18种主流方法-并附上了相关教程" aria-label="Permalink to &quot;这篇文章总结了当前的18种主流方法，并附上了相关教程&quot;">​</a></h2><p><a href="https://mp.weixin.qq.com/s/rQzCaJq1033PYWMLKpMbYQ" target="_blank" rel="noreferrer">AI视频创作十八法：超级工具合集让你轻松创作各种形式的视频！ (qq.com)</a></p><blockquote><p><strong>方法一：图片转视频双雄</strong></p><ol><li>Runway</li><li>Midjourney</li></ol></blockquote><blockquote><p><strong>方法二：文字生成视频三强</strong></p><ol><li>Runway Gen-2</li><li>Pikalabs</li><li>Zeroscope</li></ol></blockquote><blockquote><p><strong>方法三：使用Midjourney图片生成视频</strong></p><ol><li>制作3D全景视频</li><li>利用变形功能制作视频</li><li>使用Pan功能生成视频</li><li>使用Zoom Out功能制作一镜到底视频</li></ol></blockquote><blockquote><p><strong>方法四：使用其他AI软件创作视频</strong></p><ol><li>Runway frame interpolation</li><li>Genmo</li><li>Wonder Studio</li><li>Stable Diffusion</li><li>Gen-1</li><li>ControlNet</li><li>Kaiber</li><li>Deforum Colab</li></ol></blockquote><h2 id="人工智能入门指南-7月版-如何选择ai工具完成各类任务" tabindex="-1">人工智能入门指南 (7月版)，如何选择AI工具完成各类任务 <a class="header-anchor" href="#人工智能入门指南-7月版-如何选择ai工具完成各类任务" aria-label="Permalink to &quot;人工智能入门指南 (7月版)，如何选择AI工具完成各类任务&quot;">​</a></h2><p>常见使用场景下，最推荐的大模型实用工具清单，帮助你直接跳过测试环节直接使用：</p><p><strong>文本写作</strong></p><ul><li>免费选项：Bing、Claude 2</li><li>付费选项：GPT-4、ChatGPT with plugins</li></ul><p><strong>生成图像</strong></p><ul><li>透明选项：Adobe Firefly</li><li>开源选项：Stable Diffusion</li><li>免费选项：Bing / Bing Image Creator (DALL-E)、Playgound</li><li>最佳质量：Midjourney</li></ul><p><strong>思考创意</strong></p><ul><li>免费选项：Bing</li><li>付费选项：GPT-4</li></ul><p><strong>生成视频</strong></p><ul><li>动画制作：D-iD、Runway v2</li><li>声音克隆：ElevenLabs</li></ul><p><strong>处理文件和数据</strong></p><ul><li>处理文件：Claude 2</li><li>处理数据：Code Interpreter</li></ul><p><strong>获取信息和学习</strong></p><ul><li>免费选项：Bing</li><li>付费选项：Khanmigo (可汗学院提供的AI学习助手)</li></ul><p><strong>The Generative Eight：当今最具吸引力的8家人工智能公司</strong></p><ol><li><strong>Tome</strong>：帮助生成演示文稿和视觉化叙事的AI</li><li><strong>Character.AI</strong>：创造并与各种由AI生成的个性化角色对话</li><li><strong>Anthropic</strong>：一个AI安全和研究公司,为工作环境构建聊天机器人</li><li><strong>OpenAI</strong>：发布了 ChatGPT (展示了大型语言模型的力量) 和 DALL-E (用于图像生成) 的研究公司</li><li><strong>Hugging Face</strong>：为企业构建执行特定任务的模型</li><li><strong>Jasper</strong>：创建营销和品牌层面的创意内容的模型</li><li><strong>Stability AI</strong>：Stable Diffusion 的创造者,这是第一个面向公众发布的图像生成模型之一</li><li><strong>Midjourney</strong>：一个自筹资金的研究实验室，发布了一个强大的图像生成工具，完全在 Discord 上运行</li></ol><p><strong>AI Talent Race：将AI技术推向实际应用的下一代公司</strong></p><ol><li><strong>Pictor Labs</strong>：使用数字病理学 (基于AI的组织病理学) 来加速关于疾病的临床研究</li><li><strong>Endpoint Health</strong>： 使用AI辅助的精准疗法来治疗与免疫相关的疾病</li><li><strong>Otter.ai</strong>：基于AI的音频转文字，带有新的会议助手聊天机器人</li><li><strong>Bria</strong>：具有商业用途的图像和视频内容</li><li><strong>WellSaid Labs</strong>：语音合成</li><li><strong>Subtle Medical</strong>：使用AI进行图像处理的放射科学</li><li><strong>Copy.ai</strong>：通过聊天机器人进行营销文案写作</li><li><strong>Lexion</strong>：AI合同助手，适用于销售、采购、人力资源、法律等</li><li><strong>You</strong>：带有聊天机器人界面的搜索引擎</li><li><strong>OctoML</strong>：基于云的调优和运行LLM模型</li><li><strong>Runway</strong>：视频、图像和其他创意工具</li><li></li></ol>',25),e=[r];function a(s,g,u,p,c,d){return o(),i("div",null,e)}const b=l(n,[["render",a]]);export{_ as __pageData,b as default};
