import{_ as a,c as t,o as e,a4 as r}from"./chunks/framework.DBIahkuf.js";const o="/AINotes/assets/bg2023031412.DIJ4irmX.webp",d=JSON.parse('{"title":"ChatGPT衍生的项目","description":"","frontmatter":{},"headers":[],"relativePath":"Document/问答类语言模型/ChatGPT注册使用和扩展/11.ChatGPT衍生的项目.md","filePath":"Document/问答类语言模型/ChatGPT注册使用和扩展/11.ChatGPT衍生的项目.md","lastUpdated":1708941732000}'),h={name:"Document/问答类语言模型/ChatGPT注册使用和扩展/11.ChatGPT衍生的项目.md"},p=r('<h1 id="chatgpt衍生的项目" tabindex="-1">ChatGPT衍生的项目 <a class="header-anchor" href="#chatgpt衍生的项目" aria-label="Permalink to &quot;ChatGPT衍生的项目&quot;">​</a></h1><p><a href="https://github.com/TheR1D/shell_gpt" target="_blank" rel="noreferrer">shell 中使用 ChatGPT</a></p><p><a href="https://juejin.cn/post/7248531532393758778" target="_blank" rel="noreferrer">ChatGPT 开源应用月度排名 - 掘金 (juejin.cn)</a></p><p><a href="https://juejin.cn/post/7233965294837760060" target="_blank" rel="noreferrer">对AutoGPT优化后，不搭梯子，1分钟部署自己的AutoGPT - 掘金 (juejin.cn)</a></p><h2 id="聊天gpt之相关插件" tabindex="-1">聊天GPT之相关插件 <a class="header-anchor" href="#聊天gpt之相关插件" aria-label="Permalink to &quot;聊天GPT之相关插件&quot;">​</a></h2><table><thead><tr><th>插件合集</th><th><a href="https://github.com/reorx/awesome-chatgpt-api/blob/master/README.cn.md" target="_blank" rel="noreferrer">awesome-chatgpt-api/README.cn.md at master · reorx/awesome-chatgpt-api (github.com)</a></th></tr></thead><tbody><tr><td>ChatGPT浏览器插件</td><td><a href="https://chrome.google.com/webstore/detail/chatgpt-for-google/jgjaeacdkonaoafenlfkkkmbaopkbilf" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/chatgpt-for-google/jgjaeacdkonaoafenlfkkkmbaopkbilf</a></td></tr><tr><td>让ChatGPT联网插件</td><td><a href="https://chrome.google.com/webstore/detail/webchatgpt-chatgpt-with-i/lpfemeioodjbpieminkklglpmhlngfcn" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/webchatgpt-chatgpt-with-i/lpfemeioodjbpieminkklglpmhlngfcn</a></td></tr><tr><td>ChatGPT和New Bing 合并插件</td><td><a href="https://chrome.google.com/webstore/detail/chathub-all-in-one-chatbo/iaakpnchhognanibcahlpcplchdfmgma?hl=zh-CN" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/chathub-all-in-one-chatbo/iaakpnchhognanibcahlpcplchdfmgma?hl=zh-CN</a></td></tr><tr><td>ChatGPT聊天记录导出</td><td>1、安转插件:<a href="https://chrome.google.com/webstore/detail/tampermonkey/dhdgffkkebhmkfjojejmpbldmpobfkfo" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/tampermonkey/dhdgffkkebhmkfjojejmpbldmpobfkfo</a><br>2、安装脚本: <a href="https://greasyfork.org/zh-CN/scripts/456055-chatgpt-exporter" target="_blank" rel="noreferrer">https://greasyfork.org/zh-CN/scripts/456055-chatgpt-exporter</a></td></tr><tr><td>必应聊天记录导出</td><td><a href="https://github.com/gantrol/Bing-Chat-Saver" target="_blank" rel="noreferrer">https://github.com/gantrol/Bing-Chat-Saver</a></td></tr><tr><td>一秒总结B站视频主要内容</td><td>1、<a href="https://b.jimmylv.cn/video/BV1zd4y1j7Uw2%E3%80%81https://github.com/lxfater/BilibiliSummary" target="_blank" rel="noreferrer">https://b.jimmylv.cn/video/BV1zd4y1j7Uw2、https://github.com/lxfater/BilibiliSummary</a></td></tr><tr><td>ChatGPT知乎插件工具</td><td><a href="https://github.com/no13bus/chat-gpt-zhihu-extension" target="_blank" rel="noreferrer">https://github.com/no13bus/chat-gpt-zhihu-extension</a></td></tr><tr><td>音视频实时翻译</td><td><a href="https://github.com/chidiwilliams/buzz" target="_blank" rel="noreferrer">https://github.com/chidiwilliams/buzz</a></td></tr><tr><td>ChatGPT搜索引擎</td><td><a href="https://chatonai.org/" target="_blank" rel="noreferrer">https://chatonai.org/</a></td></tr><tr><td>ChatGPT for google</td><td><a href="https://chrome.google.com/webstore/detail/chatgpt-for-google/jgjaeacdkonaoafenlfkkkmbaopkbilf" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/chatgpt-for-google/jgjaeacdkonaoafenlfkkkmbaopkbilf</a></td></tr><tr><td>ChatGPT +微信</td><td><a href="https://chrome.google.com/webstore/detail/cdlangplaophialnpfbdfndiobanklfd" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/cdlangplaophialnpfbdfndiobanklfd</a></td></tr><tr><td></td><td></td></tr></tbody></table><p><a href="https://www.explainpaper.com/" target="_blank" rel="noreferrer">Explainpaper</a>（论文）：Explainpaper为您提供了论文解读服务，帮助您快速理解复杂的学术论文内容，提高阅读效率。</p><p><a href="https://www.lateral.io/" target="_blank" rel="noreferrer">LATERAL</a>（整理归类论文）：LATERAL是一款整理和归类论文的工具，帮助您将众多论文进行有效管理，方便您快速找到所需的学术资料。</p><p><a href="https://typeset.io/" target="_blank" rel="noreferrer">SCISPACE</a>（搜索论文）：SCISPACE是一款强大的论文搜索工具，助您快速查询相关领域的学术成果，满足您的研究需求。</p><p>️<a href="https://arxivxplorer.com/" target="_blank" rel="noreferrer">arXiv Xplorer</a>（（搜索论文）：arXiv Xplorer是一个针对arXiv平台的高效论文搜索引擎，让您在海量论文中轻松找到感兴趣的研究成果。</p><p><a href="http://pandagpt.io/" target="_blank" rel="noreferrer">PandaGPT</a>（帮你阅读生成知识图谱）：PandaGPT能帮助您在阅读过程中自动生成知识图谱，提升您的学习效率和理解深度。</p><p>️<a href="https://www.chatpdf.com/" target="_blank" rel="noreferrer">ChatPDF</a>（快速提取PDF信息）: ChatPDF是一款专业的PDF信息提取工具，能够帮助您高效地获取PDF文档中的重要数据和内容，提高工作效率。</p><h3 id="表格-数据库" tabindex="-1">表格&amp;数据库 <a class="header-anchor" href="#表格-数据库" aria-label="Permalink to &quot;表格&amp;数据库&quot;">​</a></h3><p><a href="https://chatexcel.com/convert" target="_blank" rel="noreferrer">ChatExcel</a>（表格）: ChatExcel是一个功能强大的表格处理工具，让您能够轻松地进行数据分析、整理和转换，助力您的数据处理工作。</p><p><a href="https://excelformulabot.com/" target="_blank" rel="noreferrer">Excelformulabot</a>（Excel公式）：Excelformulabot提供了丰富的Excel公式资源，帮助您轻松解决复杂的数据计算问题，提升Excel技能。</p><p><a href="https://promptdb.ai/" target="_blank" rel="noreferrer">PromptDB</a>（AI提示数据库）: PromptDB是一个基于AI技术的提示数据库，为您提供海量的智能提示和建议，助力您在各种场景下找到合适的解决方案。</p><h3 id="问答" tabindex="-1">问答 <a class="header-anchor" href="#问答" aria-label="Permalink to &quot;问答&quot;">​</a></h3><p><a href="https://wpdocs.chat/" target="_blank" rel="noreferrer">ChatWP</a>（Wordpress问题方案）：ChatWP专注于解决Wordpress相关问题，提供实用的方案和指南，让您在搭建和管理Wordpress网站时得心应手。</p><p>️<a href="https://www.perplexity.ai/" target="_blank" rel="noreferrer">Perplexity AI</a>（AI搜索，它只能告诉你答案）：Perplexity AI是一款AI搜索工具，特点在于只提供直接的答案，让您在寻找解决方案时更加高效准确。</p><h3 id="文本内容" tabindex="-1">文本内容 <a class="header-anchor" href="#文本内容" aria-label="Permalink to &quot;文本内容&quot;">​</a></h3><p><a href="https://magickpen.com/" target="_blank" rel="noreferrer">MagickPen</a>（文本内容）: MagickPen是一款强大的文本生成工具，利用先进的自然语言处理技术帮助用户快速生成高质量的文章、博客和其他文本内容，提高写作效率。</p><p><a href="https://www.okaaaay.com/" target="_blank" rel="noreferrer">OKaaaay</a>（文本内容）: OKaaaay是一款智能文本编辑器，通过AI技术为用户提供实时的写作建议，改善文本质量，让写作变得更加轻松。</p><p><a href="http://easy-peasy.ai/" target="_blank" rel="noreferrer">Easy-Peasy.AI</a>（文本内容）: Easy-Peasy.AI是一款便捷的AI写作助手，可以根据用户输入的关键词和需求，生成符合特定场景的文本内容，满足多样化的写作需求。</p><p><a href="https://type.ai/" target="_blank" rel="noreferrer">TypeAI</a>（文本内容）: TypeAI是一款基于人工智能的在线文本生成工具，能够理解和模仿不同的写作风格，帮助用户创作出独特的文本内容，提高创意表现力。</p><p><a href="https://copymatic.ai/" target="_blank" rel="noreferrer">copymatic</a>（文本内容）: Copymatic是一款专业的AI文案生成工具，通过分析用户提供的关键词，自动生成吸引人的广告文案、营销邮件等，助力提升市场推广效果。</p><p><a href="https://aiscreenwriter.com/" target="_blank" rel="noreferrer">AI Screenwriter</a>（改善剧本）: AI Screenwriter是一款针对剧本创作的人工智能工具，可以分析现有剧本的结构和内容，为用户提供专业的改善建议，提升剧本质量和吸引力。</p><h3 id="理解内容" tabindex="-1">理解内容 <a class="header-anchor" href="#理解内容" aria-label="Permalink to &quot;理解内容&quot;">​</a></h3><p><a href="https://b.jimmylv.cn/" target="_blank" rel="noreferrer">BiliGPT</a>（自动提取B站视频的中心思想或总结）: BiliGPT是一款针对B站视频的人工智能工具，可以快速提取视频的中心思想或总结，帮助用户更有效地理解视频内容。</p><p><a href="https://www.summarize.tech/" target="_blank" rel="noreferrer">summarize.tech: AI</a>（自动获取YouTube视频的摘要或简介）：summarize.tech: AI是一款面向YouTube视频的智能摘要工具，能够自动获取视频的摘要或简介，让用户快速了解视频的关键信息。</p><p><a href="https://www.solidpoint.ai/" target="_blank" rel="noreferrer">SolidPoint</a>（自动提取视频的中心思想或总结）: SolidPoint是一款强大的视频分析工具，可以自动提取视频的中心思想或总结，帮助用户更高效地获取视频的核心信息。</p><p><a href="https://www.humata.ai/" target="_blank" rel="noreferrer">Humata</a>（提高阅读效率、总结价值内容）: Humata是一款专业的阅读效率提升工具，通过智能技术为用户提供精炼的内容总结，让用户在短时间内掌握文章的价值信息。</p><h3 id="翻译" tabindex="-1">翻译 <a class="header-anchor" href="#翻译" aria-label="Permalink to &quot;翻译&quot;">​</a></h3><p><a href="https://fsys.app/" target="_blank" rel="noreferrer">风声雨声</a>: 风声雨声是一款专业的AI翻译工具，支持多种语言之间的实时翻译，为用户提供高质量、准确的翻译结果，帮助用户克服语言障碍，实现跨语言的沟通。</p><h3 id="浏览器插件" tabindex="-1">浏览器插件 <a class="header-anchor" href="#浏览器插件" aria-label="Permalink to &quot;浏览器插件&quot;">​</a></h3><p><a href="https://www.thomas.io/detect-gpt" target="_blank" rel="noreferrer">Detect GPT</a>（实时检查内容真实性）: Detect GPT是一款实时检查内容真实性的AI工具，能够识别由人工智能生成的文本，帮助用户辨别信息的可靠性。</p><p><a href="https://www.fluentify.io/" target="_blank" rel="noreferrer">Fluentify</a>（划字提问）: Fluentify是一款智能划词提问工具，可以帮助用户在阅读时快速提出问题，提高阅读理解能力。</p><p><a href="https://chrome.google.com/webstore/detail/arxivgpt/fbbfpcjhnnklhmncjickdipdlhoddjoh?hl=en" target="_blank" rel="noreferrer">ArxivGPT</a>（总结论文并提供关键见解）: ArxivGPT是一款针对学术论文的AI工具，能够自动生成论文摘要并提供关键见解，帮助用户快速了解研究成果。</p><p><a href="https://sharegpt.com/" target="_blank" rel="noreferrer">ShareGPT</a>（免费GPT）: ShareGPT是一款免费的GPT平台，提供多种基于AI的文本生成服务，为用户提供便捷的内容创作解决方案。</p><p><a href="https://harpa.ai/" target="_blank" rel="noreferrer">HARPA AI</a>（网页自动化）: HARPA AI是一款网页自动化工具，利用人工智能技术实现网站内容管理、数据抓取和自动化任务处理，提高工作效率。</p><p><a href="https://chatgptwriter.ai/" target="_blank" rel="noreferrer">Gmail AI</a>（谷歌邮箱生成邮件内容）: Gmail AI是一款基于AI的谷歌邮箱邮件内容生成工具，可以自动生成专业、高质量的电子邮件文本，提高邮件撰写效率。</p><h2 id="chat2db" tabindex="-1">Chat2DB <a class="header-anchor" href="#chat2db" aria-label="Permalink to &quot;Chat2DB&quot;">​</a></h2><p>Chat2DB 是一款有开源免费的多数据库客户端工具，支持windows、mac本地安装，也支持服务器端部署，web网页访问。和传统的数据库客户端软件Navicat、DBeaver 相比Chat2DB集成了AIGC的能力，能够将自然语言转换为SQL，也可以将SQL转换为自然语言，可以给出研发人员SQL的优化建议，极大的提升人员的效率，是AI时代数据库研发人员的利器，未来即使不懂SQL的运营业务也可以使用快速查询业务数据、生成报表能力。</p><p><a href="https://github.com/alibaba/Chat2DB" target="_blank" rel="noreferrer">https://github.com/alibaba/Chat2DB</a></p><h2 id="chatpdf" tabindex="-1">ChatPDF <a class="header-anchor" href="#chatpdf" aria-label="Permalink to &quot;ChatPDF&quot;">​</a></h2><p>它可以提取 PDF 文件中的内容，并将内容交给 ChatGPT 进行分析。待解析完成之后，你便可以跟它进行一对一对话交流，就好像它是一个完全理解内容的人一样。 <a href="https://www.chatpdf.com/" target="_blank" rel="noreferrer">https://www.chatpdf.com/</a> ❓</p><h2 id="chatdoc" tabindex="-1">ChatDoc <a class="header-anchor" href="#chatdoc" aria-label="Permalink to &quot;ChatDoc&quot;">​</a></h2><p>ChatDOC是一个基于ChatGPT的文件阅读助手，它可以从pdf中快速提取、定位和总结信息。 <a href="https://chatdoc.com/" target="_blank" rel="noreferrer">https://chatdoc.com/</a></p><p><a href="https://y3if3fk7ce.feishu.cn/docx/K2ASdLaDcoeOTVx7dArcrX2XnHf" target="_blank" rel="noreferrer">⁢用ChatGPT快速阅读论文：3个步骤让你轻松阅读论文文档 - 飞书云文档 (feishu.cn)</a></p><h2 id="chat论文" tabindex="-1">Chat论文 <a class="header-anchor" href="#chat论文" aria-label="Permalink to &quot;Chat论文&quot;">​</a></h2><p>利用chatgpt进行论文总结+润色+审稿+审稿回复 <a href="https://github.com/kaixindelele/ChatPaper" target="_blank" rel="noreferrer">https://github.com/kaixindelele/ChatPaper</a></p><h2 id="chatgpt-学术优化" tabindex="-1">ChatGPT 学术优化 <a class="header-anchor" href="#chatgpt-学术优化" aria-label="Permalink to &quot;ChatGPT 学术优化&quot;">​</a></h2><p>科研工作专用ChatGPT拓展，特别优化学术Paper润色体验，支持自定义快捷按钮，支持markdown表格显示，Tex公式双显示，代码显示功能完善，新增本地Python工程剖析功能/自我剖析功能 <a href="https://github.com/xianyu110/chatgpt_academic" target="_blank" rel="noreferrer">https://github.com/xianyu110/chatgpt_academic</a></p><h2 id="chatppt" tabindex="-1">ChatPPT <a class="header-anchor" href="#chatppt" aria-label="Permalink to &quot;ChatPPT&quot;">​</a></h2><p>ChatPPT是面向PPT使用者提供的AI生成PPT产品，基于ChatGPT与韦尼克模型，面向PPT用户提供可通过自然语言指令与Chat模式进行PPT文档创作的AI创作服务。目前有在线体验版、<strong>Office插件版</strong>两类，可在官网申请加入公测。</p><blockquote><p><a href="https://chat-ppt.com/" target="_blank" rel="noreferrer">chat-ppt.com</a></p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/faea8328085a4a148af3ce6063997f64~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><p>命令式一键生成PPT <a href="http://chatppt.yoo-ai.com/?share_code=148936" target="_blank" rel="noreferrer">http://chatppt.yoo-ai.com/?share_code=148936</a></p><p>图文 <a href="https://juejin.cn/post/7202984088185307191" target="_blank" rel="noreferrer">https://juejin.cn/post/7202984088185307191</a></p><p>让PPT演示表达更高效 <a href="http://motion.yoo-ai.com/" target="_blank" rel="noreferrer">http://motion.yoo-ai.com/</a></p><h2 id="一键总结-youtube-音视频内容" tabindex="-1">一键总结 YouTube 音视频内容 <a class="header-anchor" href="#一键总结-youtube-音视频内容" aria-label="Permalink to &quot;一键总结 YouTube 音视频内容&quot;">​</a></h2><p><a href="https://b.jimmylv.cn/video/BV1uM411P7oA?spm_id_from=333.1007.tianma.2-1-4.click" target="_blank" rel="noreferrer">https://b.jimmylv.cn/video/BV1uM411P7oA?spm_id_from=333.1007.tianma.2-1-4.click</a></p><h2 id="openai-translator" tabindex="-1">openai-translator <a class="header-anchor" href="#openai-translator" aria-label="Permalink to &quot;openai-translator&quot;">​</a></h2><p>我开发了一个 Bob 的插件 bob-plugin-openai-translator 使用 ChatGPT API 在 macOS 上进行全局划词翻译， 但是由于很多用户并不是 macOS 用户，所以特此开发了一个浏览器插件方便非 macOS 用户使用 ChatGPT 进行划词翻译。 <a href="https://github.com/yetone/openai-translator?utm_campaign=explore-email&amp;utm_medium=email&amp;utm_source=newsletter&amp;utm_term=daily" target="_blank" rel="noreferrer">https://github.com/yetone/openai-translator?utm_campaign=explore-email&amp;utm_medium=email&amp;utm_source=newsletter&amp;utm_term=daily</a></p><h3 id="chatgpt插件" tabindex="-1">chatGPT插件 <a class="header-anchor" href="#chatgpt插件" aria-label="Permalink to &quot;chatGPT插件&quot;">​</a></h3><ol><li>WebChatGPT：可以搜索在线信息。 <a href="https://chrome.google.com/webstore/detail/webchatgpt-chatgpt-with-i/lpfemeioodjbpieminkklglpmhlngfcn" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/webchatgpt-chatgpt-with-i/lpfemeioodjbpieminkklglpmhlngfcn</a></li><li>AIPRM for ChatGPT：收集了大量的prompt。 <a href="https://chrome.google.com/webstore/detail/aiprm-for-chatgpt/ojnbohmppadfgpejeebfnmnknjdlckgj/related" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/aiprm-for-chatgpt/ojnbohmppadfgpejeebfnmnknjdlckgj/related</a></li></ol><p>3.可以自动朗读ChatGPT的回复 <a href="https://www.jb51.net/softs/867661.html#downintro2" target="_blank" rel="noreferrer">https://www.jb51.net/softs/867661.html#downintro2</a></p><p>4.ChatGPT Prompt Genius是一款 Chrome 扩展程序，这款工具支持ChatGPT 对话历史记录保存或者导出，可以使用扩展的提示模板功能轻松查找提示并将其添加到您的收藏中，用户可以直接在页面上搜索、分类和选择提示。 ChatGPT Prompt Genius <a href="https://chrome.google.com/webstore/detail/chatgpt-prompt-genius/jjdnakkfjnnbbckhifcfchagnpofjffo" target="_blank" rel="noreferrer">https://chrome.google.com/webstore/detail/chatgpt-prompt-genius/jjdnakkfjnnbbckhifcfchagnpofjffo</a> 国内链接: <a href="https://www.jb51.net/softs/862436.html" target="_blank" rel="noreferrer">https://www.jb51.net/softs/862436.html</a></p><ol><li>强大的 Bing Chat/AI 绕过每回合条数限制，无限制的BingChat，找回Sydney，加密聊天，prompt 预设管理器 <a href="https://greasyfork.org/zh-CN/scripts/461220-powerful-bing-chat-ai" target="_blank" rel="noreferrer">https://greasyfork.org/zh-CN/scripts/461220-powerful-bing-chat-ai</a></li><li></li></ol><p><a href="https://github.com/gragland/chatgpt-everywhere" target="_blank" rel="noreferrer">ChatGPT Everywhere</a>：Chrome 瀏覽器擴充套件，在任何輸入框使用 ChatGPT。（<a href="https://twitter.com/gabe_ragland/status/1599466486422470656" target="_blank" rel="noreferrer">demo</a>）</p><h3 id="随时在任何页面上调出聊天对话框" tabindex="-1">随时在任何页面上调出聊天对话框 <a class="header-anchor" href="#随时在任何页面上调出聊天对话框" aria-label="Permalink to &quot;随时在任何页面上调出聊天对话框&quot;">​</a></h3><p><a href="https://github.com/xianyu110/chatGPTBox" target="_blank" rel="noreferrer">https://github.com/xianyu110/chatGPTBox</a></p><h3 id="ai-0x0" tabindex="-1">AI 0x0 <a class="header-anchor" href="#ai-0x0" aria-label="Permalink to &quot;AI 0x0&quot;">​</a></h3><p>一个多模态多模型通用型的全能 AI 查询生成桌面悬浮助手应用，支持 MacOS 和 Windows，可在任意桌面软件使用 AI 能力。 <a href="https://github.com/mushan0x0/AI0x0.com" target="_blank" rel="noreferrer">https://github.com/mushan0x0/AI0x0.com</a></p><h2 id="基于chatgpt-api-开发的项目" tabindex="-1">基于ChatGPT API 开发的项目 <a class="header-anchor" href="#基于chatgpt-api-开发的项目" aria-label="Permalink to &quot;基于ChatGPT API 开发的项目&quot;">​</a></h2><h3 id="whispe-免费生成音频字幕" tabindex="-1">Whispe：免费生成音频字幕 <a class="header-anchor" href="#whispe-免费生成音频字幕" aria-label="Permalink to &quot;Whispe：免费生成音频字幕&quot;">​</a></h3><p>这是一款免费生成音频字幕的工具。跟剪映相比，好处在于免费且你不需要联网，在本地可以给音频添加字幕。不用担心视频泄露。</p><p>网址：<a href="https://github.com/Const-me/Whisper" target="_blank" rel="noreferrer">github.com/Const-me/Wh…</a></p><h3 id="chatgpt的开源客户端" tabindex="-1">ChatGPT的开源客户端 <a class="header-anchor" href="#chatgpt的开源客户端" aria-label="Permalink to &quot;ChatGPT的开源客户端&quot;">​</a></h3><p>ChatGPT桌面版，根据ChatGPT地址上套了一个壳，可以像本地应用一样打开使用，比较方便。另外他还支持导出聊天记录</p><p>网址：<a href="https://github.com/lencx/ChatGPT" target="_blank" rel="noreferrer">github.com/lencx/ChatG…</a></p><h3 id="智能文案小助手" tabindex="-1">智能文案小助手 <a class="header-anchor" href="#智能文案小助手" aria-label="Permalink to &quot;智能文案小助手&quot;">​</a></h3><p><a href="https://github.com/CatsAndMice/lanlan" target="_blank" rel="noreferrer">智能文案小助手</a></p><p><img src="'+o+'" alt="img"></p><p>一个开源的 ChatGPT 前端表单页面，输入提示，自动生成文案。</p><h3 id="writely" tabindex="-1">Writely <a class="header-anchor" href="#writely" aria-label="Permalink to &quot;Writely&quot;">​</a></h3><p><a href="https://github.com/anc95/writely" target="_blank" rel="noreferrer">Writely</a>：Chrome 浏览器插件，类似于 Notion AI，支持在任意编辑器网页上进行写作辅助</p><h3 id="chaty" tabindex="-1">Chaty <a class="header-anchor" href="#chaty" aria-label="Permalink to &quot;Chaty&quot;">​</a></h3><p><a href="https://github.com/cosin2077/chaty" target="_blank" rel="noreferrer">Chaty</a>：一个命令行工具，支持使用一条命令，完成 ChatGPT 的各种相关操作</p><h3 id="read-something" tabindex="-1">Read Something <a class="header-anchor" href="#read-something" aria-label="Permalink to &quot;Read Something&quot;">​</a></h3><p><a href="https://github.com/ReadSomething/ReadSomething" target="_blank" rel="noreferrer">Read Something</a></p><p><img src="https://cdn.beekka.com/blogimg/asset/202306/bg2023060114.webp" alt="img"></p><p>一个开源的 Chrome 插件，可以将网页转成阅读模式，并且内置了 AI 总结、翻译、Mardown 转换等功能</p><h3 id="stockai-trade-基于-chatgpt-的-ai-选股工具" tabindex="-1">StockAI.Trade：基于 ChatGPT 的 AI 选股工具 <a class="header-anchor" href="#stockai-trade-基于-chatgpt-的-ai-选股工具" aria-label="Permalink to &quot;StockAI.Trade：基于 ChatGPT 的 AI 选股工具&quot;">​</a></h3><p><a href="https://stockai.trade/" target="_blank" rel="noreferrer">StockAI.Trade</a></p><p><img src="https://cdn.beekka.com/blogimg/asset/202307/bg2023071315.webp" alt="img"></p><p>基于 ChatGPT 的 AI 选股工具（仅限中国 A 股）</p><h3 id="ai-面试助手" tabindex="-1">AI 面试助手 <a class="header-anchor" href="#ai-面试助手" aria-label="Permalink to &quot;AI 面试助手&quot;">​</a></h3><p><a href="https://interview.sofasay.com/" target="_blank" rel="noreferrer">AI 面试助手</a></p><p><img src="https://cdn.beekka.com/blogimg/asset/202306/bg2023062807.webp" alt="img"></p><p>只需职位、职位描述以及简历信息，就可以开始模拟面试，AI 扮演面试官角色提问。</p><h3 id="ai-法律助手" tabindex="-1">AI 法律助手 <a class="header-anchor" href="#ai-法律助手" aria-label="Permalink to &quot;AI 法律助手&quot;">​</a></h3><p>在线地址：<a href="https://law-cn-ai.vercel.app/" target="_blank" rel="noreferrer">AI 法律助手</a></p><p>开源地址：<a href="https://github.com/lvwzhen/law-cn-ai" target="_blank" rel="noreferrer">https://github.com/lvwzhen/law-cn-ai</a></p><p><img src="https://cdn.beekka.com/blogimg/asset/202304/bg2023042701.webp" alt="img"></p><p>作者将中国法律法规喂给 ChatGPT，做成的法律问答引擎</p><h3 id="chatpaper" tabindex="-1">ChatPaper <a class="header-anchor" href="#chatpaper" aria-label="Permalink to &quot;ChatPaper&quot;">​</a></h3><p>速读 PDF 和刷 ArXiv 论文魔法器</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/81a2f13f030642b7bf33fedf5b7b27d8~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><p>ChatPaper 是一款论文总结工具，<strong>根据用户输入的关键词，自动在arxiv上下载最新的论文，再利用 ChatGPT3.5 的API接口的总结能力，将论文总结为固定的格式</strong>。</p><p>ChatPaper 能够以最少的文本，最低的阅读门槛，为大家提供最大信息量，以决定该精读哪些文章。</p><p><strong>一般一个晚上就可以速通一个小领域的最新文章</strong>。祝大家在这个极速变化的时代中，能够和AI一起进化！</p><blockquote><p>🌏 <a href="https://huggingface.co/spaces/wangrongsheng/ChatPaper" target="_blank" rel="noreferrer"><strong>Hugging Face 在线体验</strong></a></p><p>🌏 <a href="https://github.com/kaixindelele/ChatPaper" target="_blank" rel="noreferrer"><strong>GitHub · ChatPaper</strong></a></p><p>🌏 <a href="https://zhuanlan.zhihu.com/p/613055271" target="_blank" rel="noreferrer"><strong>知乎 · 安装与使用教程</strong></a></p><p>🌏 <a href="https://www.bilibili.com/video/BV1EM411x7Tr" target="_blank" rel="noreferrer"><strong>B站 · 教程视频</strong></a></p><p>这个功能免费，且代码开源，大家放心使用！</p></blockquote><h3 id="myshell英语口语练习" tabindex="-1">MyShell英语口语练习 <a class="header-anchor" href="#myshell英语口语练习" aria-label="Permalink to &quot;MyShell英语口语练习&quot;">​</a></h3><p>MyShell官网：<a href="https://app.myshell.ai/zh/robot-workshop" target="_blank" rel="noreferrer">MyShell</a></p><p>Shell是一款基于ChatGPT的AI英语口语聊天工具，有4款机器人可以选择，语音非常接近真人，理解能力超强。</p><p>点击底部录制按钮后，可以立马得到语音回复。点击 Text 可以看到文字，点击 Translate 可以将回复翻译成英文。学习英语口语的利器！</p><h3 id="openai-translator-是一款基于-chatgpt-api-的-划词翻译浏览器插件-跨平台桌面端应用" tabindex="-1">OpenAI Translator 是一款基于 ChatGPT API 的 划词翻译浏览器插件 &amp; 跨平台桌面端应用 <a class="header-anchor" href="#openai-translator-是一款基于-chatgpt-api-的-划词翻译浏览器插件-跨平台桌面端应用" aria-label="Permalink to &quot;OpenAI Translator 是一款基于 ChatGPT API 的 划词翻译浏览器插件 &amp; 跨平台桌面端应用&quot;">​</a></h3><p>使用 ChatGPT API 进行划词翻译和文本润色，借助了 ChatGPT 强大的翻译能力，帮助用户更流畅地阅读外语和编辑外语。允许跨 55 种不同语言进行相互翻译、润色和总结，支持浏览扩展、Mac 和 Windows 等等。 <img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d07912f6db954ff2b41293e9b7259a7d~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><blockquote><p>🌏<a href="https://github.com/yetone/openai-translator" target="_blank" rel="noreferrer"><strong>OpenAI Translator GitHub</strong></a>，🌏<a href="https://chrome.google.com/webstore/detail/openai-translator/ogjibjphoadhljaoicdnjnmgokohngcc" target="_blank" rel="noreferrer"><strong>Chrome 插件下载</strong></a></p></blockquote><p>🌏<a href="https://mp.weixin.qq.com/s/MosxYTyn71iZniY3Sz1QAQ" target="_blank" rel="noreferrer"><strong>OpenAI-translator: 基于 ChatGPT 的划词翻译及润色应用</strong></a></p><p>作者全程围观了 @yetone 开发 openai-translator 这款应用的全部历程，眼看着为 Bob 开发的插件在Github 和 Hacker News 上爆火，然后被推出成为浏览器插件和桌面应用，基本覆盖了 Bob 的功能😂</p><p>文章对应用的<strong>翻译(translate)</strong> 、<strong>润色(polish)</strong> 、<strong>总结(summarize)</strong> 功能进行了测评，并提供了步骤详细的浏览器插件安装教程，逐步演示操作结果，非常详细！</p><h3 id="bibigpt-哔哔终结者" tabindex="-1">BibiGPT 哔哔终结者 <a class="header-anchor" href="#bibigpt-哔哔终结者" aria-label="Permalink to &quot;BibiGPT 哔哔终结者&quot;">​</a></h3><p><a href="https://b.jimmylv.cn/" target="_blank" rel="noreferrer"><strong>BibiGPT</strong></a> <strong>是一个基于gpt-3.5-turbo的</strong>一键总结 B 站、油管、播客、本地文件的视频、音频内容网站。</p><p>BibiGPT操作方式非常简单，可以复制视频链接到该网站进行总结，也可以在原视频的基础上修改域名后缀即可快速总结。</p><p>BibiGPT还提供了部分设置，允许用户自定义输出内容格式。</p><p>BibiGPT的输出总结有<strong>笔记视图</strong>和<strong>思维导图视图</strong>，还允许<strong>将总结一键导入Notion，快速整理笔记</strong>。哇！这两个功能实在太爱了有木有！！！</p><p><img src="https://p1-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/29071e9c35db47a3bd7e78375cf245a2~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp?" alt="image.png"></p><h2 id="🤖-『chatgpt这把火是怎么烧起来-』我们分析了github近20-000个开源项目" tabindex="-1">🤖 『ChatGPT这把火是怎么烧起来？』我们分析了GitHub近20,000个开源项目 <a class="header-anchor" href="#🤖-『chatgpt这把火是怎么烧起来-』我们分析了github近20-000个开源项目" aria-label="Permalink to &quot;🤖 『ChatGPT这把火是怎么烧起来？』我们分析了GitHub近20,000个开源项目&quot;">​</a></h2><p>ChatGPT 毫无疑问是当红炸子鸡，彷佛一夜之间，全世界都在讨论 GPT、LLM、AIGC 这些火热的概念。<strong>那这把火到底是怎么烧起来？它目前有哪些主要的关注方向？未来可能还会怎么发展呢？</strong></p><p>@陈希章 对 GitHub 上近两万个项目进行了数据分析。作者在持续更新这个系列，首批部分关键洞察如下：</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/39fbc9841d304235a38cfc64e56c2ac7~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><p>2022年12月，ChatGPT 相关项目明显增加，并在2023年实现彻底爆发，3月2日更是创建了单日最多的 497 个项目。目前 17K 名作者创建了 19.4K 个项目，获得约 530K Star，80K fork，530K watch。</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d2fd90a4e7dd4a28a9bbfd3a790b9276~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><p>Star总数Top 10项目中，Prompt Engineering 最是热门，毕竟是当务之急！Star增长速度最快的项目，榜首是微软的 visual-chatgpt，更有多个很有意思的新项目榜上有名。</p><p>创建 ChatGPT 活跃项目的作者榜Top 20中，除去地理位置未知的5个作者，<strong>中国的账号是7个，占到将近 50%的比例（7/15）。</strong> 使用最多语言的前6名是 <strong>Python、TypeScript、HTML、JavaScript、Rust、Go</strong>。</p><blockquote><p>作者在 🌍<a href="https://mp.weixin.qq.com/s/BSLnp3Jx6Fs53SvQrDxHtw" target="_blank" rel="noreferrer"><strong>原文</strong></a> 表示后续会继续进行数据分析并公开数据集。我们一起期待更火热的洞察吧~</p></blockquote><h2 id="『chat应用汇总-1-在线应用-』国内优秀的产品和开发者" tabindex="-1">『Chat应用汇总 (1-在线应用)』国内优秀的产品和开发者 <a class="header-anchor" href="#『chat应用汇总-1-在线应用-』国内优秀的产品和开发者" aria-label="Permalink to &quot;『Chat应用汇总 (1-在线应用)』国内优秀的产品和开发者&quot;">​</a></h2><p>🧰 【Chat 效率工具清单】生产力工具与行业应用大全 🕘 持续更新至2023年4月27日</p><p>💬 【Chat ➕ XX】工具的图文详细介绍 👉 <a href="https://mp.weixin.qq.com/s/v8eiaCo5-uqWFVVufkAsnQ" target="_blank" rel="noreferrer">【Chat效率工具汇总】日常工作自动化，幸福感提升N倍 | 星球热帖</a></p><p>🌊🌊🌊 免费使用</p><p>▶【Chat ➕ pdf】chatPDF：支持不同语言之间互动的文件阅读助手，可以快速从上传的研究论文、书籍、手册等文件中提取、定位和汇总文件信息，并以聊天的方式给出答案 <a href="https://www.chatpdf.com/" target="_blank" rel="noreferrer">https://www.chatpdf.com/</a></p><p>▶【Chat ➕ pdf】ChatDOC：同上，目前免费额度更高 <a href="https://chatdoc.com/" target="_blank" rel="noreferrer">ChatDOC - Chat with your documents</a></p><p>▶【Chat ➕ PPT】ChatPPT：可以通过自然语言指令与Chat模式进行PPT文档创作（公测申请中，需要排队） <a href="https://chat-ppt.com/" target="_blank" rel="noreferrer">https://chat-ppt.com</a></p><p>▶【Chat ➕ Excel】ChatExcel：用对话的形式实现复杂的 Excel 操作（Chat-to-Excel），并支持一键导出成标准 Excel 表格 <a href="https://chatexcel.com/" target="_blank" rel="noreferrer">酷表ChatExcel</a></p><p>▶【Chat ➕ 论文】ChatPaper：根据关键词自动下载arxiv最新论文，并生成摘要，帮助快速判断哪些论文需要精读 <a href="https://chatpaper.org/" target="_blank" rel="noreferrer">ChatPaper</a></p><p>▶【 Chat ➕ 文档】Chatbase：基于给定文档 (如.pdf、txt、.doc和.docx) 构建和自定义聊天机器人 <a href="https://www.chatbase.co/" target="_blank" rel="noreferrer">Chatbase | ChatGPT for your website</a></p><p>🌊🌊🌊 付费使用</p><p>▶【Chat ➕ 音视频】BibiGPT：一键总结 YouTube / Bilibili 、网页、播客、会议、本地音视频等音视频内容，并支持输出端的个性化设置 <a href="https://b.jimmylv.cn/" target="_blank" rel="noreferrer">BibiGPT · AI 音视频内容一键总结</a></p><p>▶【Chat ➕ 网页】ChatWebpage：通过Al分析网站，提供网站基本信息 (如联系方式) 和高级话题(如SEO和分析)的准确答案（需要付费使用） <a href="https://chatwebpage.com/" target="_blank" rel="noreferrer">ChatWebpage | chatwebpage.com</a></p><p>▶【Chat ➕ Notion】ChatThing： <a href="https://chatthing.ai/" target="_blank" rel="noreferrer">Chat Thing | Custom ChatGTP bots using your data</a></p><p>🌊🌊🌊 社区反馈暂时无法正常使用</p><p>▶【Chat ➕ SQL】SQLChat： <a href="https://www.sqlchat.ai/" target="_blank" rel="noreferrer">https://www.sqlchat.ai/</a></p><h2 id="🤖-『chatflow』打造个性化-chatgpt-流程-构建自动化" tabindex="-1">🤖 『ChatFlow』打造个性化 ChatGPT 流程，构建自动化 <a class="header-anchor" href="#🤖-『chatflow』打造个性化-chatgpt-流程-构建自动化" aria-label="Permalink to &quot;🤖 『ChatFlow』打造个性化 ChatGPT 流程，构建自动化&quot;">​</a></h2><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/2031a1d48fe64c3192ddaf0aa519f0bb~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><p>🌍<a href="https://www.clickprompt.org/zh-CN/" target="_blank" rel="noreferrer"><strong>ClickPrompt</strong></a> 是一款专为 Prompt 编写者设计的工具 ，它支持多种基于 Prompt 的 AI 应用，例如 Stable Diffusion、ChatGPT 和 GitHub Copilot 等。使用 ClickPrompt，可以轻松地查看、分享和一键运行这些模型，同时提供在线的 Prompt 生成器，使用户能够根据自己的需求轻松创建符合要求的 Prompt，并与其他人分享。</p><p>🌍<a href="https://www.clickprompt.org/zh-CN/click-flow/" target="_blank" rel="noreferrer"><strong>ChatFlow</strong></a> 是抽取 ClickPrompt 核心能力构建的新框架，是一个围绕 ChatGPT 构建的简易工作流引擎，将做事的套路工具化，结合 AI 进行自动化。目前包括 <strong>ClickPrompt Workflow、软件系统设计、Domain Driven Design、REPL Code Kotlin、模糊的需求到代码骨架、写作</strong>等几项自动化工作流。</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/f7c5ebf671f4428f95bd7eacf6ef015d~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><p>以写作为例，作者构建了一个逐步运行的工作流，将写作流程拆解为思路扩展、继续思考、合适的标题、设计大纲、编写内容、写总结等6个步骤，并给出了更具体的提示方向，一步步引导最终的内容生成。</p><h3 id="chatpdf-pdf阅读和问答工具" tabindex="-1">ChatPDF:PDF阅读和问答工具 <a class="header-anchor" href="#chatpdf-pdf阅读和问答工具" aria-label="Permalink to &quot;ChatPDF:PDF阅读和问答工具&quot;">​</a></h3><p>网址：<a href="https://www.chatpdf.com" target="_blank" rel="noreferrer">www.chatpdf.com</a></p><p>打开即可使用，你把想要它能够帮你总结PDF文件扔给它，它会自动解析并生成一些关键性问题。然后你可以针对这些关键内容，或者你想问的问题进行提问。它都能按照文章内容给你解答。有一个沟通技巧，就是让它总结的时候，一段一段给出回复。比如：&quot;请用10段文字总结这篇文章，每段xx字，先发第一段。&quot;</p><p>基于 ChatGPT API 的读取 PDF 工具 ChatPDF：可以直接把需要阅读的 pitch deck、白皮书、文档、论文、图书喂给它，让它做总结和提问。</p><p>ChatPDF 简直是学习神器，上传 PDF 文件后，可以对它提问任何关于这份 PDF 的问题，非常适合快速提取各种 paper 论文的摘要，也支持中文输出。</p><p>上传pdf即可，然后你就可以基于pdf的内容去提问了</p><p>备注：免费版本有限制，120页PDF、不高于10M，每天3个PDF文件，一天最多提50个问题。但基本够用。</p><p>ChatPDF是一款强大的PDF阅读和问答工具，支持不同语言间的互动，原理是分析时创建PDF段落的语义 索引，回答问题时找到最相关段落并利用OpenAl的API生成答案，每天可免费处理3个PDF (最多120 页)。</p><blockquote><p><a href="https://www.chatpdf.com/" target="_blank" rel="noreferrer">www.chatpdf.com/</a></p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a27cf483deb84a309ca902c86a1b96db~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><h3 id="chatdoc-1" tabindex="-1">ChatDOC <a class="header-anchor" href="#chatdoc-1" aria-label="Permalink to &quot;ChatDOC&quot;">​</a></h3><p><strong>ChatDOC</strong> 是一个基于 ChatGPT 的文件阅读助手，支持中英文，可以快速从上传研究论文、书籍、手册等文件中提取、定位和汇总文件信息，并通过聊天的方式在几秒钟内给出问题的答案。ChatDOC 目前免费，文件大小限制为 200 页，最多可以上传 5 个文档。在即将更新的版本中，还支持跨多个文档的综合查询和问答。一把子期待了！</p><blockquote><p><a href="https://chatdoc.com/" target="_blank" rel="noreferrer">chatdoc.com/</a></p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/b1c62437b2164592ae431908c74cd2dd~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><h3 id="chatexcel" tabindex="-1">ChatExcel <a class="header-anchor" href="#chatexcel" aria-label="Permalink to &quot;ChatExcel&quot;">​</a></h3><p>ChatExcel，只需要与它聊聊天，表格中的数据可自动实现处理，也让 Excel 从一款低代码工具直接变成了零代码工具，再也不用记任何复杂的函数了。 <a href="https://chatexcel.com/" target="_blank" rel="noreferrer">https://chatexcel.com/</a></p><p>ChatExcel 由北京大学深圳研究生院/信息工程学院3位硕博生独立开发，主打用对话的形式实现复杂的 Excel 操作（Chat-to-Excel），并支持一键导出成标准 Excel 表格。目前已开放Demo供大家试玩，不用注册、网页上就能用、不限使用次数，相当利好懒人和 Excel 小白。</p><blockquote><p><a href="https://chatexcel.com/" target="_blank" rel="noreferrer">chatexcel.com/</a></p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/07839833d05845038c7ef2f32580cf3b~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><h3 id="chatwebpage" tabindex="-1">ChatWebpage <a class="header-anchor" href="#chatwebpage" aria-label="Permalink to &quot;ChatWebpage&quot;">​</a></h3><p>ChatWebpage是一款智能网站分析与问答工具，通过Al分析网站，提供网站相关问题的准确答案，输入网址即可开始聊天。可回答关于网站的各类问题，包括基本信息 (如联系方式) 和高级话题(如SEO和分析)。</p><blockquote><p><a href="https://chatwebpage.com/" target="_blank" rel="noreferrer">chatwebpage.com/</a></p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/f12d258ac3d24db6b374f77f300aaa9c~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><h3 id="chatvox" tabindex="-1">ChatVox <a class="header-anchor" href="#chatvox" aria-label="Permalink to &quot;ChatVox&quot;">​</a></h3><p>ChatVox 让你能够与任何视频或音频文件进行互动，只需输入YouTube或播客链接，就可以像与ChatGPT聊天一样与视频展开对话。国内开发者的产品，官方介绍本地功能正在火速开发中，期待ing</p><blockquote><p><a href="https://chatvox.aitodo.co/" target="_blank" rel="noreferrer">chatvox.aitodo.co/</a></p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a6c71b3eba014627913bbbddfd61ac11~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><h3 id="scichat" tabindex="-1">SciChat <a class="header-anchor" href="#scichat" aria-label="Permalink to &quot;SciChat&quot;">​</a></h3><p>SciChat是一款基于前沿语言大模型的AI聊天机器人，能快速响应任何你感兴趣的话题，由科研创新服务平台SciRadar 打造。SciChat 目前每人每日拥有10,000字符的免费使用额度。</p><blockquote><p><a href="https://www.sciradar.com/" target="_blank" rel="noreferrer">www.sciradar.com/</a></p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/09e8e5a7f8034b73bc54d21910cd198a~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><h3 id="flowus-ai" tabindex="-1">FlowUs AI <a class="header-anchor" href="#flowus-ai" aria-label="Permalink to &quot;FlowUs AI&quot;">​</a></h3><p>Flowus的AI写作功能是针对各种内容类型的。它可以帮助你写文章、论文、报告、甚至新闻稿等内容。除了根据用户输入的关键词、主题和目的进行信息筛选和整理之外，该功能还可以帮助用户自动生成标题、文章大纲、段落、句子等内容，帮助用户更快速、更准确地完成文章的创作。</p><blockquote><p><a href="https://flowus.cn/" target="_blank" rel="noreferrer">flowus.cn/</a></p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c059b04725114f6bbf45f7db117aa993~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><h3 id="风声雨声" tabindex="-1">风声雨声 <a class="header-anchor" href="#风声雨声" aria-label="Permalink to &quot;风声雨声&quot;">​</a></h3><p>风声雨声包含书籍翻译、书籍阅读、知名作者书籍推荐3个功能，支持epub、pdf、word、srt、txt 在内的多种最常见的文件格式，基于 GPT-3.5完成多种语言的书籍翻译、书籍信息快速提取与问答、推荐作者书籍等。</p><blockquote><p><a href="https://fsys.app/" target="_blank" rel="noreferrer">fsys.app/</a></p></blockquote><h3 id="chatmind" tabindex="-1">ChatMind <a class="header-anchor" href="#chatmind" aria-label="Permalink to &quot;ChatMind&quot;">​</a></h3><p>根据文本提示生成思维导图，还支持表格和写作</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9be5f5555b324d4cbca159c7bf319070~tplv-k3u1fbpfcp-zoom-in-crop-mark:1512:0:0:0.awebp" alt="img"></p><p>ChatMind 是一个 AI 辅助工具，可以像操作 ChatGPT 一样，输入文本并生成对应主题的思维导图，可以实现一键演示、框架梳理、头脑风暴、项目管理、日程安排、笔记总结等多种功能，还支持一键保存。</p><p>此外，ChatMind 还支持与 AI 对话生成表格、处理表格数据、生成文字段落、，并提供了可以参考的写作模板，帮助更快地完成工作。</p><p>试了一下，对于给定具体一个书名的输入提示，ChatMind 生成的大纲跟实际内容对不上。不过，围绕某个话题给出的思维导图，还是很靠谱的，逻辑非常清晰。如图是根据“2023年读50本书”的目标给出的规划，还行！</p><p><a href="https://www.chatmind.tech/" target="_blank" rel="noreferrer">ChatMind.Tech</a>：<a href="https://www.theverge.com/2023/4/13/23681796/amazon-ai-coding-assistant-codewhisperer-microsoft" target="_blank" rel="noreferrer">https://www.theverge.com/2023/4/13/23681796/amazon-ai-coding-assistant-codewhisperer-microsoft</a></p><h3 id="albus自动生成图文知识图谱" tabindex="-1">Albus自动生成图文知识图谱 <a class="header-anchor" href="#albus自动生成图文知识图谱" aria-label="Permalink to &quot;Albus自动生成图文知识图谱&quot;">​</a></h3><p>Albus官网：<a href="https://www.albus.org/" target="_blank" rel="noreferrer">https://www.albus.org/</a></p><p>完全免费，结合了ChatGPT和Obsidian的功能，可以作为你的专属知识探索引擎，帮你快速出视觉化的知识图谱涵盖各个领域</p><ul><li>【比思维导图更强大！三分钟学会用GPT自动生成图文知识图谱学遍一切】<a href="https://www.bilibili.com/video/BV1mc41137Bu?vd_source=36c9491a7fa2ab8a22ca060af01b7472" target="_blank" rel="noreferrer">https://www.bilibili.com/video/BV1mc41137Bu?vd_source=36c9491a7fa2ab8a22ca060af01b7472</a></li><li>【《ChatGPT自学的正确打开方式 》 小学、初中到成人教育全覆盖】<a href="https://www.bilibili.com/video/BV1h14y127ND?vd_source=36c9491a7fa2ab8a22ca060af01b7472" target="_blank" rel="noreferrer">https://www.bilibili.com/video/BV1h14y127ND?vd_source=36c9491a7fa2ab8a22ca060af01b7472</a></li></ul>',208),l=[p];function i(n,c,s,b,f,m){return e(),t("div",null,l)}const u=a(h,[["render",i]]);export{d as __pageData,u as default};
