<span class="line"><span style="color:#E06C75;">    res</span><span style="color:#56B6C2;"> =</span><span style="color:#C678DD;"> await</span><span style="color:#E5C07B;"> api</span><span style="color:#ABB2BF;">.</span><span style="color:#61AFEF;">sendMessage</span><span style="color:#ABB2BF;">(</span><span style="color:#E5C07B;">data</span><span style="color:#ABB2BF;">.</span><span style="color:#E06C75;">message</span><span style="color:#ABB2BF;">)</span></span>
